import React, { useState, useEffect } from 'react';
import './App.css';
import Login from './pages/Login';
import { getStoredUser, getStoredToken, logout } from './services/auth';
import {
  addConversation,
  getAllConversations,
  getConversationsByCategory,
  getConversationsSortedByTime,
  deleteConversation,
  updateConversation,
  getAllConversationsPaginated,
  getConversationsByCategoryPaginated,
  getConversationsSortedByTimePaginated,
  seedDummyData,
} from './services/api';

function App() {
  const [conversations, setConversations] = useState([]);
  const [prompt, setPrompt] = useState('');
  const [response, setResponse] = useState('');
  const [category, setCategory] = useState('General');
  const [filter, setFilter] = useState('All Conversations');
  const [apiError, setApiError] = useState(false);
  const [editingId, setEditingId] = useState(null);
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [user, setUser] = useState(() => getStoredUser());
  const [isAuthenticated, setIsAuthenticated] = useState(() => !!getStoredToken());

  // Pagination state
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [totalElements, setTotalElements] = useState(0);
  const [pageSize, setPageSize] = useState(5);
  const [sortBy, setSortBy] = useState('timestamp');
  const [sortDir, setSortDir] = useState('desc');

  useEffect(() => {
    if (isAuthenticated) {
      loadConversations();
    }
  }, [isAuthenticated]);

  const loadConversations = async (page = currentPage, size = pageSize, sort = sortBy, direction = sortDir) => {
    setLoading(true);
    setErrorMessage('');
    try {
      let data;
      if (filter === 'All Conversations') {
        data = await getAllConversationsPaginated(page, size, sort, direction);
      } else if (filter === 'sorted') {
        data = await getConversationsSortedByTimePaginated(page, size);
      } else {
        data = await getConversationsByCategoryPaginated(filter, page, size, sort, direction);
      }

      setConversations(data.content || []);
      setTotalPages(data.totalPages || 0);
      setTotalElements(data.totalElements || 0);
      setCurrentPage(data.number || 0);
      setApiError(false);
    } catch (error) {
      console.error('Error loading conversations:', error);
      setConversations([]);
      setApiError(true);
      setErrorMessage(error.message || 'Failed to load conversations');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!prompt.trim() || !response.trim()) return;

    setLoading(true);
    setErrorMessage('');
    try {
      const conversationData = {
        prompt,
        response,
        category,
        timestamp: editingId ? conversations.find(c => c.id === editingId)?.timestamp : new Date().toISOString(),
      };

      if (editingId) {
        await updateConversation(editingId, conversationData);
        setEditingId(null);
      } else {
        await addConversation(conversationData);
      }

      setPrompt('');
      setResponse('');
      setCategory('General');
      await loadConversations();
    } catch (error) {
      console.error('Error saving conversation:', error);
      setErrorMessage(error.message || 'Failed to save conversation');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this conversation?')) return;

    setLoading(true);
    setErrorMessage('');
    try {
      await deleteConversation(id);
      await loadConversations();
    } catch (error) {
      console.error('Error deleting conversation:', error);
      setErrorMessage(error.message || 'Failed to delete conversation');
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (conv) => {
    setPrompt(conv.prompt);
    setResponse(conv.response);
    setCategory(conv.category);
    setEditingId(conv.id);
  };

  const handleCancelEdit = () => {
    setPrompt('');
    setResponse('');
    setCategory('General');
    setEditingId(null);
  };

  const handleFilterChange = async (e) => {
    const value = e.target.value;
    setFilter(value);
    setCurrentPage(0); // Reset to first page when filter changes
    await loadConversations(0, pageSize, sortBy, sortDir);
  };

  const handlePageChange = async (newPage) => {
    setCurrentPage(newPage);
    await loadConversations(newPage, pageSize, sortBy, sortDir);
  };

  const handlePageSizeChange = async (e) => {
    const newSize = parseInt(e.target.value);
    setPageSize(newSize);
    setCurrentPage(0);
    await loadConversations(0, newSize, sortBy, sortDir);
  };

  const handleSortChange = async (e) => {
    const newSortBy = e.target.value;
    setSortBy(newSortBy);
    setCurrentPage(0);
    await loadConversations(0, pageSize, newSortBy, sortDir);
  };

  const handleSortDirectionChange = async () => {
    const newDirection = sortDir === 'desc' ? 'asc' : 'desc';
    setSortDir(newDirection);
    setCurrentPage(0);
    await loadConversations(0, pageSize, sortBy, newDirection);
  };

  const handleSeedData = async () => {
    setLoading(true);
    setErrorMessage('');
    try {
      await seedDummyData();
      await loadConversations(0, pageSize, sortBy, sortDir);
      setErrorMessage('30 dummy conversations added successfully!');
    } catch (error) {
      console.error('Error seeding data:', error);
      setErrorMessage(error.message || 'Failed to seed data');
    } finally {
      setLoading(false);
    }
  };

  const handleLogin = (userData) => {
    setUser(userData);
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    logout();
    setUser(null);
    setIsAuthenticated(false);
    setConversations([]);
  };

  const getRoleBadge = () => {
    if (!user) return null;
    const roleColors = {
      'ROLE_ADMIN': '#e74c3c',
      'ROLE_RESEARCHER': '#3498db',
      'ROLE_ENGINEER': '#9b59b6',
      'ROLE_PREMIUM': '#f39c12',
      'ROLE_USER': '#95a5a6'
    };
    const roleNames = {
      'ROLE_ADMIN': 'Admin',
      'ROLE_RESEARCHER': 'Researcher',
      'ROLE_ENGINEER': 'Engineer',
      'ROLE_PREMIUM': 'Premium',
      'ROLE_USER': 'User'
    };
    return (
      <span className="role-badge" style={{ backgroundColor: roleColors[user.role] }}>
        {roleNames[user.role]}
      </span>
    );
  };

  // Show login page if not authenticated
  if (!isAuthenticated) {
    return <Login onLogin={handleLogin} />;
  }

  return (
    <div className="app-container">
      <div className="header">
        <div className="header-top">
          <div>
            <h1>ChatGPT Conversation Manager</h1>
            <p>Organize your AI chats efficiently</p>
          </div>
          <div className="user-info">
            <div>
              <span className="username">Welcome, {user.username}!</span>
              {getRoleBadge()}
            </div>
            <button className="logout-btn" onClick={handleLogout}>Logout</button>
          </div>
        </div>
        {apiError && (
          <div style={{color: 'orange', padding: '10px', background: '#fff3cd', border: '1px solid #ffeaa7', borderRadius: '4px', margin: '10px 0'}}>
            ⚠️ Backend API is not available. Please ensure the Spring Boot server is running on port 8081.
          </div>
        )}
        {errorMessage && (
          <div style={{color: '#721c24', padding: '10px', background: '#f8d7da', border: '1px solid #f5c6cb', borderRadius: '4px', margin: '10px 0'}}>
            ❌ {errorMessage}
          </div>
        )}
        {loading && (
          <div style={{color: '#004085', padding: '10px', background: '#cce5ff', border: '1px solid #99ccff', borderRadius: '4px', margin: '10px 0'}}>
            ⏳ Loading...
          </div>
        )}
      </div>

      <form className="chat-form" onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter prompt"
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          required
        />
        <textarea
          placeholder="Enter response"
          value={response}
          onChange={(e) => setResponse(e.target.value)}
          required
        />
        <select value={category} onChange={(e) => setCategory(e.target.value)}>
          <option value="General">General</option>
          <option value="Education">Education</option>
          <option value="Coding">Coding</option>
          <option value="Career">Career</option>
        </select>
        <button type="submit" disabled={loading}>{editingId ? 'Update' : 'Add'} Conversation</button>
        {editingId && <button type="button" onClick={handleCancelEdit} disabled={loading}>Cancel</button>}
      </form>

      <div className="controls">
        <div className="control-row">
          <select value={filter} onChange={handleFilterChange}>
            <option value="All Conversations">All Conversations</option>
            <option value="General">General</option>
            <option value="Technical">Technical</option>
            <option value="Creative">Creative</option>
            <option value="Business">Business</option>
            <option value="Education">Education</option>
            <option value="Health">Health</option>
            <option value="Travel">Travel</option>
            <option value="Food">Food</option>
            <option value="sorted">Sorted by Time</option>
          </select>

          <select value={pageSize} onChange={handlePageSizeChange}>
            <option value={5}>5 per page</option>
            <option value={10}>10 per page</option>
            <option value={15}>15 per page</option>
            <option value={20}>20 per page</option>
          </select>

          <select value={sortBy} onChange={handleSortChange}>
            <option value="timestamp">Sort by Time</option>
            <option value="category">Sort by Category</option>
            <option value="prompt">Sort by Prompt</option>
          </select>

          <button 
            className="sort-direction-btn" 
            onClick={handleSortDirectionChange}
            title={`Sort ${sortDir === 'desc' ? 'Ascending' : 'Descending'}`}
          >
            {sortDir === 'desc' ? '↓' : '↑'}
          </button>

          <button className="seed-btn" onClick={handleSeedData} disabled={loading}>
            🌱 Add 30 Dummy Data
          </button>
        </div>
      </div>

      <div className="chat-list">
        {conversations.length === 0 ? (
          <p className="empty">No conversations found</p>
        ) : (
          conversations.map((conv) => (
            <div key={conv.id} className="chat-card">
              <h3>{conv.prompt}</h3>
              <div className="response">{conv.response}</div>
              <div className="meta">
                Category: {conv.category} | Time: {new Date(conv.timestamp).toLocaleString()}
              </div>
              <div className="actions">
                <button className="edit-btn" onClick={() => handleEdit(conv)}>Edit</button>
                <button className="delete-btn" onClick={() => handleDelete(conv.id)}>Delete</button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default App;
